import React from "react";
import { connect } from "react-redux";
import { doincrement, dodecrement, doreset } from "../action";
import "../main.css";

class Counter extends React.Component {
  render() {
    let { number, step, dispatch } = this.props;
    return (
      <div>
        <center>
          <div className="counter-div">
            <h1>Counter</h1>

            <h2 className="number">{number}</h2>
            <button onClick={() => dispatch(doincrement())} className="buttons">
              Increment
            </button>
            <button onClick={() => dispatch(dodecrement())} className="buttons">
              Decrement
            </button>
            <button onClick={() => dispatch(doreset())} className="buttons">
              Reset
            </button>
          </div>
          <div className="step">
            <h3>Step</h3>
            <button className="buttons">+5</button>
            <button className="buttons">+20</button>
            <button className="buttons">+60</button>
            <button className="buttons">+80</button>
          </div>
          <div className="max">
            <h3>max</h3>

            <button className="buttons">+100</button>
            <button className="buttons">+300</button>
            <button className="buttons">+500</button>
            <button className="buttons">+800</button>
          </div>
        </center>
      </div>
    );
  }
}
function mapStateToProps(state) {
  return {
    number: state.number
  };
}

export default connect(mapStateToProps)(Counter);
