const initial_state = {
  number: 0
};

export default function reducer(state = initial_state, action) {
  switch (action.type) {
    case "Increment":
      return {
        ...state,
        number: state.number + 1
      };

    case "Decrement":
      return {
        ...state,
        number: state.number - 1
      };

    case "Reset":
      return {
        ...state,
        number: 0
      };
    default:
      return state;
  }
}
